class TreeNode:    def __init__(self, data):        self.data = data        self.left =None        self.right = None    def in_order(self):        res = []        if self.left:            res += self.left.in_order()        res.append(self.data)        if self.right:            res += self.right.in_order()        return res    def pre_order(self):        res = []        res.append(self.data)        if self.left:            res += self.left.pre_order()        if self.right:            res += self.right.pre_order()        return res    def post_order(self):        res = []        if self.left:            res += self.left.post_order()        if self.right:            res += self.right.post_order()        res.append(self.data)        return resroot = TreeNode(1)root.left = TreeNode(2)root.right = TreeNode(3)root.left.left = TreeNode(4)root.left.right = TreeNode(5)root.right.left = TreeNode(6)root.right.right = TreeNode(7)print(root.post_order())