def bubble_sort(ls):    for x in range(len(ls)):        for i in range(len(ls)-1):            left = ls[i]            right = ls[i+1]            if left > right:                 ls[i], ls[i + 1] = right, left    return ls# print(bubble_sort([1,2,3,4,1,8]))def selection_sort(lst):    for step in range(len(lst)):        min_index = step        for i in range(step + 1, len(lst)):            if lst[i] < lst[step]:                lst[step], lst[i] = lst[i], lst[step]    return lstprint(selection_sort([6,5,4,3,2]))