def increase(val : str) -> str:    ls = []    for i in val:        ls.append(i)    last = ls[-1]    try:        num = int(last)        ls.remove(last)        ls.append(str(num + 1))    except:        ls.append('1')    x = ''.join(ls)    return x# print(increase('hello49'))# print(increase('hello'))def mergeAlternately(word1: str, word2: str) -> str:    ls = []    for i in word1:            ls.append(i)            for x in word2:                ls.append(x)    return lsprint(mergeAlternately('zxcv', 'sdqwer'))